import React,{useState,useEffect} from "react";

const AuthContext=React.createContext({
    isLoggedIn:false,
    onLogout:()=>{},
    onLogin:(email,password,clg)=>{}
})

export const AuthContextProvider=(props)=>{
    const[isLoggedIn,setIsLoggedIn]=useState(false)
    useEffect(()=>{const storedloginuser=localStorage.getItem('isLoggedIn')
    if(storedloginuser==='1'){
      setIsLoggedIn(true)
    }} ,[])
    const logoutHandler=()=>{
        localStorage.removeItem('isLoggedIn')
        setIsLoggedIn(false)
    }
    const loginHandler=()=>{
        localStorage.setItem('isLoggedIn',1)
        setIsLoggedIn(true)
    }
    return(
        <AuthContext.Provider value={{isLoggedIn:isLoggedIn,onLogout:logoutHandler,onLogin:loginHandler}}>{props.children}</AuthContext.Provider>
    )
}
export default AuthContext;
